var title = datalist[i].getElementsByTagName("a")[0].title;
var imgurl = datalist[i].getElementsByTagName("img")[0].getAttribute("src");
if (imgurl.indexOf('http:') != 0) {
imgurl = "http:" + imgurl;
}
var content = datalist[i].getElementsByTagName("img")[0].alt;
var obj = {
"title": title,
"detailUrl": href,
"posterUrl": imgurl,
"content": content
};
